mapper.select:
select etss.id as id,xc.name as cityName,etss.type,etmc.maintain_name, min_mileage as minMileage,max_mileage as maxMileage,format(score/10,1) as score ,modifier,creator, DATE_FORMAT(etss.create_time,"%Y-%m-%d") as create_time, etmc.id as maintainType , xc.code as cityCode from e_task_score_strategy etss INNER JOIN x_city xc on etss.city_code = xc.code LEFT JOIN e_task_maintain_category etmc on etmc.id = etss.maintain_type and etmc.is_enabled=1 where 1=1 and etss.is_show='1'
and etss.type = #{type}
and etss.maintain_type = #{maintain}
and etss.city_code = #{cityCode}
order by etss.city_code,etss.type,etss.maintain_type
limit #{from,jdbcType=INTEGER}, #{to,jdbcType=INTEGER}

select COUNT(1) from
e_task_score_strategy etss
LEFT JOIN e_task_maintain_category etmc on etmc.id = etss.maintain_type  and etmc.is_enabled=1
where 1=1

  and etss.type = #{type}



  and etss.maintain_type = #{maintain}



  and etss.city_code = #{cityCode}

select   from e_task_score_strategy etss
where etss.city_code = #{cityCode} and etss.type = #{type}

  and etss.maintain_type =#{maintainType}

 select  from e_task_score_strategy etss
where etss.city_code = #{cityCode} and etss.type = #{type}
and  (#{minMileage} BETWEEN etss.min_mileage and etss.max_mileage  or #{maxMileage} BETWEEN etss.min_mileage and etss.max_mileage )
